import React, { useState, useEffect } from "react";
import Todos from "./Todos";
import Header from "../components/layout/Header";
import AddTodo from "./AddTodo";
import Footer from "../store/containers/Footer";
//khai báo thư viện axios
import axios from "axios";

function TodoApp() {
  const [state, setState] = useState({
    todos: []
  });
  const handleCheckboxChange = id => {
    setState({
      todos: state.todos.map(todo => {
        if (todo.id === id) {
          todo.completed = !todo.completed;
        }
        return todo;
      })
    });
  };
  const deleteTodo = id => {
    axios
      .delete(`https://jsonplaceholder.typicode.com/todos/${id}`)
      .then(reponse =>
        setState({
          todos: [
            ...state.todos.filter(todo => {
              return todo.id !== id;
            })
          ]
        })
      );
  };

  const addTodo = title => {
    const todoData = {
      title: title,
      completed: false
    };
    axios
      .post("https://jsonplaceholder.typicode.com/todos", todoData)
      .then(response => {
        console.log(response.data);
        setState({
          todos: [...state.todos, response.data]
        });
      });
  };

  useEffect(() => {
    const config = {
      params: {
        _limit: 5
      }
    };
    // tạo GET request để lấy danh sách todos
    axios
      .get("https://jsonplaceholder.typicode.com/todos", config)
      .then(response => setState({ todos: response.data }));
  }, []);

  return (
    <div className="container">
      <Header />
      <AddTodo addTodo={addTodo} />
      <Todos
        todos={state.todos}
        handleChange={handleCheckboxChange}
        deleteTodo={deleteTodo}
      />
      <Footer />
    </div>
  );
}
export default TodoApp;
